import React, { Component } from 'react';

export default class productType extends Component {
  constructor(props){
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.onChangeType = this.onChangeType.bind(this);
    this.onChangeAttribute = this.onChangeAttribute.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

  this.state= {
    type: '',
    book:'',
    size: '',
    height: '',
    width: '',
    lenght: ''

  }
}

onChangeType(e) {
  this.setState({
      type:e.target.value
  })
}

onChangeAttribute(e) {
  this.setState({
    [e.target.name]:e.target.value,

  })
}

onSubmit(e) {
  e.preventDefault();

  const obj = {

    [e.target.name]:this.state.value
         
  }
  console.log(this.state.value);
}

    renderSelectedForm(type) {
        switch(type) {
            
      
          case '1':
            return  <form name="form_name1" >
                    <label for="fname">Weight</label>
                    <input type="text" name="book"
                    value={ this.state.book}
                    onChange={this.onChangeAttribute}></input>
                    </form>
        
      
          case '2':
            return  <form name="form_name1" id="2" >
                    <label for="fname">Size(MB)</label>
                    <input type="text" name="size"
                    value = {this.state.size}  
                    onChange={this.onChangeAttribute}></input>
                    </form>;
      
          case '3':
            return  <form name="form_name1" id="3"  >
                    <label for="height">Height</label>
                    <input type="number"  name="height" required
                    onChange={this.onChangeAttribute}/>
      
                    <label for="width">Width</label>
                    <input type="number"  name="width" required
                    onChange={this.onChangeAttribute}/>
      
                    <label for="length">Length</label>
                    <input type="number"  name="length" required 
                    onChange={this.onChangeAttribute}/>
                  </form>;
            
          default:
            return null;
      
           
        }
      }
      
       handleChange(e) { this.setState({type: e.target.value}); }
      

    render() {
    return (
        <div>

        <form >
           <select id="type" value={this.state.type} onChange={this.handleChange}>
               <option value="0" selected="selected">Select</option>
               <option value="1">Book</option>
               <option value="2">DVD</option>
               <option value="3">Furniture</option>
           </select>
        </form>
        {this.renderSelectedForm(this.state.type)}

        <button onClick={this.onSubmit}>ADD</button>
    </div>
    )
}

}
