import React,{Component} from "react";
import './product_list';
import axios from 'axios';
import '../scss_files/product_list_style.scss';

 class RecordList extends Component {
     constructor(props){
         super(props)
         
     }


    
     handleChange = (e) => {
        const checked = e.target.checked;
        const selectedColor = e.target.name;
        if(checked) {
          this.setState(prevState => ({
            colors: [...prevState.colors, selectedColor]
          }));
        } else {
          this.setState(prevState => ({
            colors: prevState.colors.filter(color => color!==selectedColor)
          }));
        }
      }
        
    

    render() {        
        return (
            
           
            <div className = 'box'>
        
            <input className = 'checkbox'
             name="red"
             type="checkbox"
             onChange={this.handleChange} />

                <td className = 'a'>{this.props.obj.d_sku}</td>
               
                <td className = 'a'>{this.props.obj.d_name}</td>
                
                <td className = 'a'>{this.props.obj.d_price + "$"}</td>
                
                <td className = 'a'>{this.props.obj.d_attribute}</td>
            </div>
            
        );
    }
}

export default RecordList;
