import axios from 'axios';
import '../scss_files/product_list_style.scss';
import React from 'react';
import {withRouter} from 'react-router-dom';
import { Component } from 'react';
//import RecordList  from './List';

 class product_list extends Component {
    handleClick() {
        this.props.history.push('/add_product');
      }

    constructor(props) {
        super(props);
        this.state = {products: []};
        this.handleChecked.this.handleChecked.bind()
     
      }


    componentDidMount(){
        axios.get('http://localhost/scandiweb-test/src/backend/list.php')
        .then(response => {
            this.setState({ products: response.data});
        })
        .catch(function (error) {
            console.log(error);
        })
    }
    
   
     usersList(){

        return this.state.products.map(function(object,i){
           
           return  <div className = 'box'>
                 
                 <input type="checkbox" onChange={handleChecked()} />
            
                      <td className = 'a'>{i.object.d_sku}</td>
    
                      <td className = 'a'>{i.object.d_name}</td>
            
                      <td className = 'a'>{i.object.d_price + "$"}</td>
            
                      <td className = 'a'>{i.object.d_attribute}</td>
            
                    </div>  

        });
     }


     handleChecked () {
        this.setState({checked: !this.state.checked});
    }

      

     nextPath(path) {
        this.props.history.push(path);
     }
    


    render() {
        return (
            <div>
                <h3 className = 'title'>Product List</h3>
            

                <button class="button-add" role="button" onClick={() => this.nextPath('/add_product') }>ADD</button>
                <button class="button-delete" role="button" >DELETE</button>
                
                <div className = 'test'></div>
                
                <table className = "table-page" >
                    <tbody className = 'test2'>
                        
                   {this.usersList()}
                    </tbody>
                </table>                 
            
            </div>
        )
    }
}

export default withRouter (product_list);
