import axios from 'axios';
import '../scss_files/product_list_style.scss';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import RecordList from './List';
import { Component } from 'react';

 class product_list extends Component {
    constructor(props) {
        super(props);
        this.state = {products: []};
    }

    componentDidMount(){
        axios.get('http://localhost/scandiweb-test/src/backend/list.php')
        .then(response => {
            this.setState({ products: response.data});
        })
        .catch(function (error) {
            console.log(error);
        })
    }
  

    usersList(){
        return this.state.products.map(function(object, i){
            return <RecordList obj={object} key={i}/>;
        });
    }

    render() {
        return (
            <div>
                <h3>Product List</h3>

                <button type="button" class="btnadd btn-outline-dark">Add</button>
                <button type="button" class="btnDelel btn-outline-dark">Add</button>

                <table className = "table-striped" >
                    <tbody>
                   {this.usersList()}
                    </tbody>
                    </table>

                    
            
            </div>
        )
    }
}

export default product_list;
